    "POINTSPREAD": 0,
    "TOTAL": 1,
    "MONEYLINE": 2,
    // Add more output names and indexes as needed
};

let outputIndex;

function outputMappedSend(currentResult, outputIndex) {
    // Create an array to store messages for each output, initialized with null values
    const outputs = Array(Object.keys(outputMapping).length).fill(null);

    // Construct a new message object for the output
    var outputMessage = {
        req: incomingMessage.req, // Spread operator to copy existing msg properties
        res: incomingMessage.res,
        transform1Message: {combinedResults : incomingMessage.combinedResults, resultsArray : incomingMessage.resultsArray},
        transform2Message: currentResult,
        extractMessage: incomingMessage.extractMessage
    };

    // Ensure the extractMessage doesn't carry over unnecessary properties
    if (outputMessage.extractMessage) {
        delete outputMessage.transform1Message.extractMessage;
    }


    // Assign the new message to the appropriate output based on the index
    outputs[outputIndex] = outputMessage;

    // Send the messages. Only the targeted output index will have a non-null message.
    node.send([outputs]);
}

