var incomingMessageVar = msg;
var SportsSettingsVar = context.global.get('SportsSettings');
var transform2Message = incomingMessageVar.transform2Message;
var main = {};
var vigMax = 5;
var globalStake = 100;
var arbMin = .05;
var arbMax = .07;


main.sport = transform2Message['Sport'];

// Initialize arrays to hold values for each line
main.line = {};

//-----
//SUBFUNCTIONS
function americanToDecimal(americanOdds) {
    // Ensure americanOdds is a number
    americanOdds = parseFloat(americanOdds);

    // Check if americanOdds is a valid number
    if (isNaN(americanOdds)) {
        throw new Error("Invalid input. Please provide a valid number for American odds.");
    }

    // Convert American odds to decimal odds
    let decimalOdds;
    if (americanOdds >= 100) {
        decimalOdds = (americanOdds / 100) + 1;
    } else {
        decimalOdds = (100 / Math.abs(americanOdds)) + 1;
    }

    return decimalOdds.toFixed(3); // Round to 3 decimal places for precision
}

function calculateArbitrage(oddsOutcome1, oddsOutcome2, globalStake) {
    let line1Value = americanToDecimal(oddsOutcome1);
    let line2Value = americanToDecimal(oddsOutcome2);


    // Convert string to float for safety
    oddsOutcome1 = parseFloat(line1Value);
    oddsOutcome2 = parseFloat(line2Value);

    // Determine the lowest odds and calculate stakes
    let riskForLowestOdds = globalStake; // The stake for the outcome with the lowest odds is set to $100
    let lowestOdds = Math.min(oddsOutcome1, oddsOutcome2);
    let highestOdds = Math.max(oddsOutcome1, oddsOutcome2);


    // Calculate Arbitrage Percentage
    let arbPercentageTotal = (1 / oddsOutcome1 + 1 / oddsOutcome2) * 100;

    let result = {}; // Initialize the result object

    // Determine If There Is an Arbitrage Opportunity
    if (arbPercentageTotal < 100) {
        // Adjust the other stake proportionally to maintain arbitrage
        let riskOnHighestOdds = (lowestOdds / highestOdds) * riskForLowestOdds;

        // Round stakes to the nearest dollar
        let roundedRiskOnLowestOdds = Math.round(riskForLowestOdds);
        let roundedRiskOnHighestOdds = Math.round(riskOnHighestOdds);

        // Calculate total required stake to maintain arbitrage proportions
        let totalRoundedStake = roundedRiskOnLowestOdds + roundedRiskOnHighestOdds;

        // Calculate total required stake to maintain arbitrage proportions
        let ArbTotalStake = roundedRiskOnLowestOdds + roundedRiskOnHighestOdds;

        // Calculate potential returns for each bet
        let returnOnLowestOdds = roundedRiskOnLowestOdds * lowestOdds;
        let returnOnHighestOdds = roundedRiskOnHighestOdds * highestOdds;

        // Calculate profits for each outcome by subtracting the total stake from the returns of each outcome
        let ArbProfit1 = returnOnLowestOdds - ArbTotalStake;
        let ArbProfit2 = returnOnHighestOdds - ArbTotalStake;


        // Populate the result object with values
        result.ArbOpportunity = true;
        result.ArbPercentage = 100 - arbPercentageTotal.toFixed(2);
        result.ArbBetOnOutcome1 = roundedRiskOnLowestOdds.toFixed(2);
        result.ArbBetOnOutcome2 = roundedRiskOnHighestOdds.toFixed(2);
        result.ArbTotalStake = totalRoundedStake.toFixed(2);
        result.ArbProfit1 = ArbProfit1.toFixed(2);
        result.ArbProfit2 = ArbProfit2.toFixed(2);

    } else {
        // Indicate no arbitrage opportunity in the result object
        result.ArbOpportunity = false;
        result.message = "No Arbitrage Opportunity.";
    }

    return result;
}

//-----
// MAIN CALCULATIONS
function calculatePointsRange() {
    // Filter Range by Sports
    // Calculate Limits
    main.PointspreadPointsDifference = transform2Message['Bet1'].Line1PointsAmFloat + transform2Message['Bet2'].Line2PointsAmFloat

    main.PointspreadRangeMin = SportsSettingsVar[main.sport].PointspreadRangeMin;
    main.PointspreadRangeMax = SportsSettingsVar[main.sport].PointspreadRangeMax;

    // Calculate Range Rating
    main.PointspreadPointsDifferenceRating = main.PointspreadPointsDifference / main.PointspreadRangeMax;

    return
}

function calculateLineDifference() {
    // Cases : Middle = Loss / Push
    // -120 +140
    // Pay 120 to win 100 / Pay 100 to win 140
    // W1:-$0; L1:+$20 | W2:+$20; L2:$0

    // +100 +120
    // Pay 100 to win 100 / Pay 120 to win 140
    // W1:+$0; L1:+$20 | W2:+$20; L2:+$0

    // -110 -120
    // Pay 110 to win 100 / Pay 120 to win 120
    // W1:-$20; L1:-$10 | W2:-$10; L2:-$20  = DROP
    main.line = {};

    // Iterate over each line
    for (let i = 1; i <= 2; i++) {
        main.line[i] = transform2Message[`Bet${i}`];        
        main.line[i].lineValue = transform2Message[`Bet${i}`][`Line${i}PriceAmML`];


        if (main.line[i].lineValue < 0) {
            main.line[i].Risk = Math.abs(main.line[i].lineValue);
            main.line[i].Win = 100;
            main.line[i].Takeback = Math.abs(main.line[i].lineValue) + main.line[i].Win;
            main.line[i].Lose = Math.abs(main.line[i].lineValue);
            main.line[i].Vig = Math.abs(main.line[i].lineValue) - main.line[i].Win; 
        } else if (main.line[i].lineValue > 0) {
            main.line[i].Risk = 100;
            main.line[i].Win = main.line[i].lineValue;
            main.line[i].Takeback = main.line[i].Risk + main.line[i].Win;
            main.line[i].Lose = main.line[i].Risk;
            main.line[i].Vig = 0;            
        } else if (main.line[i].lineValue === 0) {
            main.line[i].Risk = 100;
            main.line[i].Win = 100;
            main.line[i].Takeback = main.line[i].Risk + main.line[i].Win;
            main.line[i].Lose = main.line[i].Risk;
            main.line[i].Vig = 0;
        }

    }
    main.totalTakeback = main.line[1].Takeback + main.line[2].Takeback;
    return
}

function calculateOutcomes() {
    // Cases : Middle = Loss / Push
    // -120 +140
    // Pay 120 to win 100 / Pay 100 to win 140
    // W1:-$0; L1:+$20 | W2:+$20; L2:$0

    // +100 +120
    // Pay 100 to win 100 / Pay 120 to win 140
    // W1:+$0; L1:+$20 | W2:+$20; L2:+$0

    // -110 -120
    // Pay 110 to win 100 / Pay 120 to win 120
    // W1:-$20; L1:-$10 | W2:-$10; L2:-$20  = DROP

    main.W1 = main.line[1].Takeback - main.line[1].Risk - main.line[2].Risk;
    main.L1 = main.line[2].Takeback - main.line[1].Risk - main.line[2].Risk;
    main.W2 = main.line[2].Takeback - main.line[1].Risk - main.line[2].Risk;
    main.L2 = main.line[1].Takeback - main.line[1].Risk - main.line[2].Risk;
    main.TotalVig = main.line[1].Vig + main.line[2].Vig;
    return
}

function calculateBetType() {
    // Middle / Vigsplit / Arb
    // is it a good middle?
    // is it a + vigsplit and - spread?
    // is it a + 5% arb with low outlay?

    // TO DO : 
    // 1. Calculate Arb
    let risk = 100;
    main.arbResults = calculateArbitrage(main.line[1].lineValue, main.line[2].lineValue, risk);

    // 2. Calculate VigSplit
    main.line1VSP = main.line[1].Win - main.line[1].Risk;
    main.line2VSP = main.line[2].Win - main.line[2].Risk;
    main.vigSplitProfit = main.line1VSP + main.line2VSP;
    main.vigSplitProfitRate = main.vigSplitProfit / (main.line[1].Risk + main.line[2].Risk)
    main.vigSplitTotalRisk = main.line[1].Risk + main.line[2].Risk;

    // A: If high middle, take as middle
    if (main.PointspreadPointsDifference >= main.PointspreadRangeMin
        && main.TotalVig <= vigMax) {
        main.BetType = "MIDDLEBET";
    }   
    // B: If Arb Less than Range min & greater than 0
    else if (main.PointspreadPointsDifference < main.PointspreadRangeMin
        && main.arbResults.ArbPercentage >= .05
        && main.arbResults.ArbPercentage <= .15){
        main.BetType = "ARBBET";
        //Take it.
    }
    // C: VIGSPLIT LAST RESORT : If less then Range Min & less than 5% Arb & more than 15
    else if (main.PointspreadPointsDifference < main.PointspreadRangeMin
        && main.arbResults.ArbPercentage < .05 && main.arbResults.vigSplitProfit > 15) {
        main.BetType = "VIGSPLITBET";
        //Take it.
    }
    else{
        main.BetType = false;
    }

    return
}

function calculateStake(globalStake) {

    main.arbResults = calculateArbitrage(main.line[1].lineValue, main.line[2].lineValue, globalStake);
    if (main.BetType == "MIDDLEBET"){
        main.line[1].stake = globalStake; 
        main.line[2].stake = globalStake;
    }
    else if (main.BetType == "ARBITRAGEBET"){
        main.line[1].stake = (main.arbResults.ArbBetOnOutcome1/100) * globalStake;
        main.line[2].stake = (main.arbResults.ArbBetOnOutcome2/100) * globalStake;
    }
    if (main.BetType == "VIGSPLITBET") {
        main.line[1].stake = (main.line[1].risk / 100) * globalStake;
        main.line[2].stake = (main.line[2].risk / 100) * globalStake;
    }
}
