function updateBufferWithIncomingMessage(incomingMessage) {
    // Initialize a buffer object if it doesn't exist
    if (!context.get("buffer")) {
        context.set("buffer", {});
    }

    // Initialize a timeout object to keep track of message timeouts
    if (!context.get("timeouts")) {
        context.set("timeouts", {});
    }

    // Retrieve the buffer and timeouts objects
    let buffer = context.get("buffer");
    let timeouts = context.get("timeouts");
    const timeoutDuration = 60000; // Timeout duration in milliseconds (1 minute)

    // Extract the message ID from the incoming message
    var betIDVar = incomingMessage.fullMessage._id;

    // Ensure buffer entry exists for the current message ID and initialize it as an empty array if it doesn't
    if (!buffer[betIDVar]) {
        buffer[betIDVar] = {};
        buffer[betIDVar].messages = [];
    }

    // Add the incoming message to the buffer array for this betIDVar
    buffer[betIDVar].messages.push(incomingMessage);

    // Persist the updated buffer
    context.set("buffer", buffer);

    // Return the relevant variables for use outside the function
    return {
        buffer, // Updated buffer object
        timeouts, // Updated timeouts object
        betIDVar // The message ID variable
    };
}

