            main.SameGame = "Unclear";
        }
    }
    // D: If Vigsplitbet
    if (main.BetType == "MONEYLINEBET") {

        // Filter results by BetType
        let moneylineBetMessages = buffer[betIDVar].messages.filter(message =>
            message.transform1Message && message.transform1Message.BetType === "MONEYLINEBET"
        );
        // Check if it doesn't exist
        if (moneylineBetMessages.length <= 0) {
            main.SameGame = "Clear";
        } else {
            // If not , mark it unclear
            main.SameGame = "Unclear";
        }
    }
}

function calculateProfit() {

    // CHECK MIDDLEBET
    if (incomingMessage.transform1Message.BetType == "MIDDLEBET"){
        if (incomingMessage.transform1Message.PointspreadPointsDifference >= incomingMessage.transform1Message.PointspreadRangeMin)
        {main.profitCheck = true;}
        else{ main.profitCheck = false;}
    }
    // CHECK ARBBET
    else if(incomingMessage.transform1Message.BetType == "ARBBET"){
        if (incomingMessage.transform1Message.arbResults.ArbPercentage >= arbPercentageRangeMin)
        { main.profitCheck = true; }
        else { main.profitCheck = false; }      
    }
    // CHECK VIGSPLITBET
    else if (incomingMessage.transform1Message.BetType == "VIGSPLITBET"){
        if (incomingMessage.transform1Message.vigSplitProfit >= vigSplitRangeMin) { main.profitCheck = true; }
        else { main.profitCheck = false; }
    }
    // CHECK MONEYLINEBET
    else if (incomingMessage.transform1Message.BetType == "MONEYLINEBET") {
        if (incomingMessage.transform1Message.moneylineDifference > moneylineRangeMin) { main.profitCheck = true; }
        else { main.profitCheck = false; }
    }
    // CHECK VIGMAX
    if (incomingMessage.transform1Message.TotalVig > vigMax){
        main.profitCheck = false;
    }

}


